import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Contact } from './contact';


@Injectable({
  providedIn: 'root'
})
export class UserRegistrationService {

  constructor(private http: HttpClient) { }

  public doRegistration(contact: Contact) {
    return this.http.post("http://localhost:8080/api/contacts", contact, { responseType: 'text' as 'json' });
  }
  public getUsers() {
    return this.http.get("http://localhost:9090/api/contacts");
  }
  public getUserByEmail(email: any) {
    return this.http.get("http://localhost:9090/api/contacts" + email);
  }
  public deleteUser(id: any) {
    return this.http.delete("http://localhost:/api/contacts" + id);
  }
}
